import * as signalR from '@microsoft/signalr';
import { HubConnection, HubConnectionBuilder, Subject } from '@microsoft/signalr';


class TrackInfo
{
  IsBeat: boolean = false;
}

export class SignalrService {

  private hubConnection: any;
  private $allFeed: Subject<TrackInfo> = new Subject<TrackInfo>();
  /*public startConnection2() {

    this.connection = new signalR.HubConnectionBuilder()
      .withUrl('https://localhost:7133/hub', {
        skipNegotiation: true,
        transport: signalR.HttpTransportType.WebSockets
      })
      .withAutomaticReconnect()
      .build();


    connection.start()
      .then(() => {
        console.log("connection established");


      })
      .catch((err: any) => {
        console.log("error occured" + err);

      });
  }*/

  public startConnection() {
    return new Promise((resolve, reject) => {
      this.hubConnection = new HubConnectionBuilder()
        .withUrl("https://localhost:7133/hub", {
          skipNegotiation: true,
          transport: signalR.HttpTransportType.WebSockets
        }).build();

      this.hubConnection.start()
        .then(() => {
          
          console.log("connection established");
          return resolve(true);
        })
        .catch((err: any) => {
          console.log("error occured" + err);
          reject(err);
        });
    });
  }

  public listenToAllFeeds() {
    (<HubConnection>this.hubConnection).on("GetFeed", (data: TrackInfo) => {
      this.$allFeed.next(data);
    });
  }
}
