import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { HubConnectionBuilder, IStreamSubscriber } from '@microsoft/signalr';
import { Observable, Subject } from 'rxjs';
import { SignalrService, TrackInfo } from './SignalrService';


@Component({
  selector: 'app-fetch-data',
  templateUrl: './fetch-data.component.html'
})
export class FetchDataComponent {
  public scenes: SceneModel[] = [];
  allFeedSubscription: any;



  constructor(http: HttpClient, @Inject('BASE_URL') baseUrl: string) {
    http.get<SceneModel[]>('https://localhost:7133/' + 'scenes').subscribe(result => {
      this.scenes = result;
    }, error => console.error(error));

    var signalR = new SignalrService();
    signalR.startConnection().then(() =>
    {
      signalR.listenToAllFeeds();
    });

    const subscriber: IStreamSubscriber<TrackInfo> = {
        next: (trackInfo: TrackInfo) => {
            console.log('Received TrackInfo:', trackInfo);
        },
        error: function(err: any): void {
          console.log('Error');
        },
        complete: function(): void {
          console.log('Done');
        }
    };

    this.allFeedSubscription = signalR.AllFeedObservable
      .subscribe(subscriber);

  }
}

interface SceneModel {
  
  name: string;
}
<h1 id="tableLabel">Weather forecast</h1>

<p>This component demonstrates fetching data from the server.</p>

<p *ngIf="!scenes?.length"><em>Loading...</em></p>

<table class='table table-striped' aria-labelledby="tableLabel" *ngIf="scenes.length">
  <thead>
    <tr>
      <th>Name</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let scene of scenes">
      <td>{{ scene.name }}</td>
    </tr>
  </tbody>
</table>
